  const [useList, setUserList] = useState([
    { id: 1, name: 'aaa' },
    { id: 2, name: 'bbb' },
    { id: 3, name: 'ccc' },
  ]);

  return (
    <div>
      {UserList.map((element, idx) => {
        return (
          <p key='idx'>
            <span>{element.id}</span>
            <span>{element.name}</span>
          </p>
        );
      })}
      ;
    </div>
  );
};

export default MyuseState006;
