import { useState } from 'react';

const MyuseState005 = () => {
  const [customerList, setCustomerList] = useState([
    {
      name: '박보검',
      address: '서울시 강남구',
      phone: '010-000-0000',
    },
    {
      name: '송중기',
      address: '서울시 서초구',
      phone: '010-111-1111',
    },
    {
      name: '송혜교',
      address: '서울시 개포구',
      phone: '010-222-2222',
    },
  ]);
  const [customer, setCustomer] = useState({
    name: '',
    address: '',
    phone: '',
  });
  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>이름</th>
            <th>주소</th>
            <th>전번</th>
          </tr>
        </thead>
        <tobody>
          {customerList.map((element, index) => {
            return (
              <tr key={index}>
                <td>{element.name}</td>
                <td>{element.address}</td>
                <td>{element.phone}</td>
              </tr>
            );
          })}
        </tobody>
      </table>
    </div>
  );
};

export default MyuseState005;
