const Myprops001 = ({ name }) => {
  //부모가 넘겨주는값을 받을때는 {name}처럼 받아준다.
  //자식이 부모의값을 변경못한다.읽기전용으로만 사용가능.

  return (
    <div>
      <h3>props:properties</h3>
      <p>hello,{name}</p>
    </div>
  );
};

export default Myprops001;
